// Bit reverse unsigned char bit_reverse(unsigned char x) { unsigned char newx = 0; for (int i = 0; i &lt; 8; i++) { newx |= (((x &gt;&gt; i) &amp; 1) &lt;&lt; (7-i)); } return newx; } (2) https://mp.weixin.qq.com/s/KNUH_RmIhUHhuSZLSmN4LQ
// Bit reverse(faster) // ç¢Ÿå¼äº¤æ¢æ³• unsigned char bit_reverse_faster(unsigned char x) { x = (x&lt;&lt;4) | (x&gt;&gt;4); // [ 5678 1234 ]  x = ((x&lt;&lt;2)&amp;0xcc) | ((x&gt;&gt;2)&amp;0x33); // [ 78 56 34 12 ]  x = ((x&lt;&lt;1)&amp;0xaa) | ((x&gt;&gt;1)&amp;0x55); // [ 8 7 6 5 4 3 2 1 ]  return x; } æµ‹è¯•å·¥å…·å‡½æ•°:
int to_binary_str(unsigned long val, char *bin_str, int *len) { char *p, *q; int cnt = 0; while (val) { char tmp = val &amp; 1; bin_str[cnt++] = tmp+&#39;0&#39;; val = val &gt;&gt; 1; } p = bin_str, q = bin_str+cnt-1; while (p &lt; q) { char tmp = *p; *p = *q; *q = tmp; p++, q--; } bin_str[cnt] = 0; *len = cnt; return 0; } int main(void) { char bin[65]; int len; unsigned char x = 0b11110000; unsigned char y = 0b10101010; to_binary_str(bit_reverse(x), bin, &amp;len); printf(&#34;bin: %s\n&#34;, bin); to_binary_str(bit_reverse(y), bin, &amp;len); printf(&#34;bin: %s\n&#34;, bin); assert(bit_reverse(x) == bit_reverse_faster(x)); assert(bit_reverse(y) == bit_reverse_faster(y)); return 0; }
